import React, { useState, useEffect } from "react";
import { Box, Pressable, Switch, Text, useColorMode } from "native-base";

import { View } from "react-native";
import { useSpring, animated, useSpringValue } from "@react-spring/web";

const AnimatedBackground = () => {
  const { colorMode, toggleColorMode } = useColorMode();
  console.log(colorMode);

  const colorvalue = useSpringValue();
  const [spring1, api] = useSpring({
    from: { backgroundColor: "white" },
    to: { backgroundColor: "black" },
    config: { duration: 1000 },
  });

  const handleToggle = () => {
    toggleColorMode();
    // colorMode === "light" ? colorvalue.start(1) : colorvalue.start(0);
    api.start();
  };

  return (
    <>
      <Switch
        position={"fixed"}
        zIndex="100"
        defaultIsChecked={true}
        onValueChange={() => {
          handleToggle();
        }}
      />
      <animated.div
        style={{
          // backgroundColor: colorvalue.to([0, 1], ["white", "black"]),
          height: 200,
          width: 200,
          position: "absolute",
          spring1,
        }}
      />
    </>
  );
};

export default AnimatedBackground;
