import { Box, useColorMode } from "native-base";
import React from "react";
import { Text, StyleSheet } from "react-native";
import { useSpring, animated, useSpringValue } from "@react-spring/web";
import LinearGradient from "react-native-web-linear-gradient";

const FadeMask = ({ height }) => {
  const { colorMode } = useColorMode();
  const [lightStart, setLightStart] = useSpringValue(0);
  const [lightEnd, setLightEnd] = useSpringValue(100);
  const [darkStart, setDarkStart] = useSpringValue(100);
  const [darkEnd, setDarkEnd] = useSpringValue(0);



  return (
    <LinearGradient
      style={{
        position: "absolute",
        height: 100,
        width: "100%",
        bottom: 0,
      }}
      colors={[
        `rgba(32, 32, 35, ${lightStart.toFixed(2) / 100})`,
        "white",
        `rgba(32, 32, 35, ${darkEnd.toFixed(2) / 100})`,
      ]}
    ></LinearGradient>
  );
};

export default FadeMask;
