import React, { useState } from "react";
import { Box, Center, Container, Divider, Text, VStack } from "native-base";
import { ScrollView, Dimensions } from "react-native";
import { useSpring, animated, to } from "@react-spring/web";

const width = Dimensions.get("window").width;
const height = Dimensions.get("window").height;

const itemHeight = height / 5;

const HomeScreen = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [{ opacity }, set] = useSpring(() => ({ opacity: 0 }));

  const handleScroll = (event) => {
    const scrollY = event.nativeEvent.contentOffset.y;
    const currentItemIndex = Math.floor(scrollY / itemHeight);
    setCurrentIndex(currentItemIndex);
  };

  return (
    <ScrollView
      scrollEventThrottle={16}
      showsVerticalScrollIndicator={false}
      onScroll={handleScroll}
    >
      <VStack w="600">
        {[...Array(6).keys()].map((item, index) => (
          <animated.div
            style={{
              height: itemHeight,
              color: "black",
              opacity: index === currentIndex ? opacity : 0,
            }}
          >
            Lorem Ipsum is simply dummy text of the printing and typesetting
            industry. Lorem Ipsum has been the industry's standard dummy text
          </animated.div>
        ))}
      </VStack>
    </ScrollView>
  );
};

export default HomeScreen;
