import React, { useEffect, useRef, useState } from "react";
import {
  Box,
  Center,
  Container,
  Divider,
  Text,
  VStack,
  ScrollView,
  View,
  useColorMode,
} from "native-base";
import { Dimensions } from "react-native";
import FadeMask from "../components/FadeMask";
import { useSpring, animated, useSpringValue } from "@react-spring/web";

const width = Dimensions.get("window").width;
const height = Dimensions.get("window").height;

const itemHeight = height / 5;

const HomeScreen = () => {
  const { colorMode, toggleColorMode } = useColorMode();
  const colorvalue = useSpringValue();

  const bg = useSpring({
    background: colorMode === "light" ? "white" : "#202023",
  });

  const [mousePos, setMousePos] = useState({});
  const scrollRef = useRef(null);

  const handleMouseDown = (event) => {
    setMousePos({ x: event.clientX, y: event.clientY });
  };

  const handleMouseUp = (event) => {
    const currentPos = { x: event.clientX, y: event.clientY };
    const deltaX = currentPos.x - mousePos.x;
    const deltaY = currentPos.y - mousePos.y;

    scrollRef.current.scrollTo({ x: deltaX, y: deltaY, animated: true });
  };

  useEffect(() => {
    const handleMouseMove = (event) => {
      setMousePos({ x: event.clientX, y: event.clientY });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  return (
    <ScrollView ref={scrollRef} scrollEnabled>
      {[...Array(15).keys()].map((item, index) => (
        <Box height={itemHeight}>
          <Text color={"black"}>
            Lorem Ipsum is simply dummy text of the printing and typesetting
            industry. Lorem Ipsum has been the industry's standard dummy text
            ever since the 1500s, when an unknown printer took a galley of type
            and scrambled it to make a type specimen book. It has survived not
            only five centuries, but also the leap into electronic typesetting,
            remaining essentially unchanged. It was popularised in the 1960s
            with the release of Letraset sheets containing Lorem Ipsum passages,
            and more recently with desktop publishing software like Aldus
            PageMaker including versions of Lorem Ipsum.
          </Text>
        </Box>
      ))}
      <animated.div
        style={{
          ...bg,
          width,
          height,
          position: "absolute",
          zIndex: -10,
        }}
      />
      <div
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        style={{
          width,
          height,
          position: "absolute",
          zIndex: 10,
        }}
      />
      {/* <FadeMask />; */}
    </ScrollView>
  );
};

export default HomeScreen;

