import { View, Text } from "react-native";
import React from "react";
import { Button, HStack, Pressable, useColorMode } from "native-base";
import { Dimensions } from "react-native";

import { useSpring, animated, useSpringValue } from "@react-spring/web";
import TabBarMinimal from "./TabBarMinimal";
const width = Dimensions.get("window").width;
const height = Dimensions.get("window").height;

const TabHeaderMinimal = () => {
  const { colorMode, toggleColorMode } = useColorMode();

  const bg = useSpring({
    background: colorMode === "light" ? "white" : "#202023",
  });
  return (
    <animated.div
      style={{
        ...bg,
        display: "flex",
        justifyContent: "center",
        alignItems: "center",

        width,
      }}
    >
      <Pressable w="10" h="10" bg={"gray.300"} onPress={toggleColorMode} />
    </animated.div>
  );
};

export default TabBarMinimal;
