import React, { useRef, useState } from "react";
import {
  Box,
  Center,
  Container,
  Divider,
  Text,
  VStack,
  ScrollView,
  View,
  useColorMode,
} from "native-base";
import { Dimensions } from "react-native";
import FadeMask from "../components/FadeMask";
import { useSpring, animated, useSpringValue } from "@react-spring/web";

const width = Dimensions.get("window").width;
const height = Dimensions.get("window").height;

const itemHeight = height / 5;

const HomeScreen = () => {
  const { colorMode, toggleColorMode } = useColorMode();
  const colorvalue = useSpringValue();

  const bg = useSpring({
    background: colorMode === "light" ? "white" : "#202023",
  });
  const [scrollY, setScrollY] = useState(0);
  const scrollRef = useRef(null);
  const [dragging, setDragging] = useState(false);
  const [dragStart, setDragStart] = useState(0);
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleDragStart = (event) => {
    setDragging(true);
    setDragStart(event.nativeEvent.pageY);
  };

  const handleDragEnd = () => {
    setDragging(false);
  };

  const handleDragMove = (event) => {
    if (dragging) {
      const dragMove = event.nativeEvent.pageY - dragStart;
      setScrollY((prev) => prev + dragMove);
      setDragStart(event.nativeEvent.pageY);
    }
  };

  const handleScroll = (event) => {
    setScrollPosition(event.nativeEvent.contentOffset.y);
  };

  return (
    <View
      onStartShouldSetResponder={() => true}
      onResponderGrant={handleDragStart}
      onResponderMove={handleDragMove}
      onResponderRelease={handleDragEnd}
    >
      <ScrollView ref={scrollRef} onScroll={handleScroll}>
        {[...Array(15).keys()].map((item, index) => (
          <Box height={itemHeight}>
            <Text color={"black"}>
              Lorem Ipsum is simply dummy text of the printing and typesetting
              industry. Lorem Ipsum has been the industry's standard dummy text
              ever since the 1500s, when an unknown printer took a galley of
              type and scrambled it to make a type specimen book. It has
              survived not only five centuries, but also the leap into
              electronic typesetting, remaining essentially unchanged. It was
              popularised in the 1960s with the release of Letraset sheets
              containing Lorem Ipsum passages, and more recently with desktop
              publishing software like Aldus PageMaker including versions of
              Lorem Ipsum.
            </Text>
          </Box>
        ))}

        <animated.div
          style={{
            ...bg,
            width,
            height,
            position: "absolute",
            zIndex: -10,
          }}
        />
        {/* <FadeMask />; */}
      </ScrollView>
    </View>
  );
};

export default HomeScreen;

