import React, { useState, useEffect } from "react";
import { Box, Pressable, Switch, Text, useColorMode } from "native-base";

import { View } from "react-native";
import { useSpring, animated, useSpringValue } from "@react-spring/web";

const AnimatedBackground = () => {
  const { colorMode, toggleColorMode } = useColorMode();

  const colorvalue = useSpringValue(0);
  const colorFade = useSpring({
    from: { backgroundColor: colorMode === "light" ? "white" : "black" },
    to: { backgroundColor: colorMode === "light" ? "black" : "white" },
  });

  const handleToggle = () => {
    colorFade.start();
    toggleColorMode();
  };
  // const handleToggle = () => {

  //   toggleColorMode();
  //   colorvalue.start({
  //     from: {
  //       backgroundColor: colorMode === "light" ? "black" : "white",
  //     },
  //     to: {
  //       backgroundColor: colorMode === "dark" ? "white" : "black",
  //     },
  //   });
  // };

  return (
    <>
      <Switch
        position={"fixed"}
        zIndex="100"
        onValueChange={() => {
          handleToggle();
        }}
      />
      <animated.div
        style={{
          // backgroundColor: colorvalue.to([0, 1], ["white", "black"]),
          height: 200,
          width: 200,
          ...colorFade,
        }}
      />
    </>
  );
};

export default AnimatedBackground;
