import React, { useEffect, useRef, useState } from "react";
import {
  Box,
  Center,
  Container,
  Divider,
  Text,
  VStack,
  ScrollView,
  useColorMode,
  View,
} from "native-base";
import { Dimensions } from "react-native";
import { useSpring, animated, useSpringValue } from "@react-spring/web";
const width = Dimensions.get("window").width;
const height = Dimensions.get("window").height;

const itemHeight = height / 5;

const HomeScreen = () => {
  const { colorMode, toggleColorMode } = useColorMode();
  const colorvalue = useSpringValue();
  const [mousePos, setMousePos] = useState({});
  const scrollRef = useRef(null);
  const [isDragging, setIsDragging] = useState(false);
  const [startY, setStartY] = useState(0);
  const [startScrollTop, setStartScrollTop] = useState(0);
  const [velocity, setVelocity] = useState(0);
  const bg = useSpring({
    background: colorMode === "light" ? "white" : "#202023",
  });

  useEffect(() => {
    const handleMouseMove = (event) => {
      setMousePos({ x: event.clientX, y: event.clientY });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  useEffect(() => {
    if (!isDragging) {
      scrollRef.current.scrollTop += velocity;
      setVelocity(velocity * 0.9);

      if (Math.abs(velocity) < 0.1) {
        setVelocity(0);
      }
    }
  }, [isDragging, velocity]);

  const handleMouseDown = (event) => {
    setIsDragging(true);
    setStartY(event.clientY);
    setStartScrollTop(scrollRef.current.scrollTop);
    setVelocity(0);
  };

  const handleMouseUp = (event) => {
    setIsDragging(false);
    setVelocity(
      scrollRef.current.scrollTop - startScrollTop + (event.clientY - startY)
    );
  };

  return (
    <>
      {/* <View
        style={{
          width: "100%",
          height: "100%",
          position: "fixed",
          zIndex: 100,
        }}
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        onMouseMove={handleMouseMove}
      />
      <animated.div
        style={{
          ...bg,
          width: "100%",
          height: "100%",
          position: "fixed",
          top: 0,
          left: 0,
          zIndex: -1,
        }}
      /> */}
      <ScrollView
        ref={scrollRef}
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
      >
        {[...Array(15).keys()].map((item, index) => (
          <Box height={itemHeight}>
            <Text color={"black"}>
              Lorem Ipsum is simply dummy text of the printing and typesetting
              industry. Lorem Ipsum has been the industry's standard dummy text
              ever since the 1500s, when an unknown printer took a galley of
              type and scrambled it to make a type specimen book. It has
              survived not only five centuries, but also the leap into
              electronic typesetting, remaining essentially unchanged. It was
              popularised in the 1960s with the release of Letraset sheets
              containing Lorem Ipsum passages, and more recently with desktop
              publishing software like Aldus PageMaker including versions of
              Lorem Ipsum.
            </Text>
          </Box>
        ))}

        <animated.div
          style={{
            ...bg,
            width,
            height,
            position: "absolute",
            zIndex: -10,
          }}
        />

        {/* <FadeMask />; */}
      </ScrollView>
    </>
  );
};

export default HomeScreen;

